f=open('C:/Users/av/Desktop/Bioinformatics/2/Antibiotics/RNA_codon_table_1.txt')

Data=f.read().splitlines()
print(Data)
Codons={}
for i in Data:
    Codons[i[0:3]]=i[-1]
print(Codons)

RNA=''
Peptide=Codons[RNA[0:3]]
for i in range(3, len(RNA), 3):
    if Codons[RNA[i:i+3]]==' ':
        break
    else:
        Peptide=Peptide+Codons[RNA[i:i+3]]
    i=i+3
print(Peptide)
